    return (
        <div className="todo-item" onClick={() => onToggle(id)}>
            <div>{ isDone ? (<div className="check-mark">âœ“</div>) : (<div> </div>)}</div>
            <div className={`todo-text ${isDone ? ('checked') : ('')}`}>{content}</div>
            <div className="remove-item" onClick={(e) => { e.stopPropagation(); onRemove(id); }}>&times;</div>
        </div>
    );
}

