import './Preference.css';

const Preference = () => {

    function onToggle() {
        // CSS에서 바뀜
        const navigation = document.querySelector('.navigation');
        const body = document.querySelector('body');
        const footer = document.querySelector('.footer');
        const preference = document.querySelector('.preference-template-wrapper');

        navigation.classList.toggle("dark");
        body.classList.toggle("dark");
        footer.classList.toggle("dark");
        preference.classList.toggle("dark");
    }

    return (
        <div className="preference-template-wrapper">
            <div className="title">
                <h2>기본 설정</h2>
                <p></p>
            </div>
            <div className="content">
                <ul className="general">
                    <li className="theme">
                        <span className="text">다크 테마</span>
                        <label className="switch">
                            <input type="checkbox" name="theme" onChange={onToggle}></input>
                            <span className="slider round"></span>
                        </label>
                    </li>
                </ul>
            </div>
        </div>
    );
}

export default Preference;
