import { useEffect, useState } from 'react';
import './App.css';
import DayDoListTemplate from './components/daydo/DaydoListTemplate';
import Footer from './components/main/Footer';
import Navigation from './components/main/Navigation';
import Preference from './components/preference/Preference';
import TodoListTemplate from './components/todo/TodoListTemplate';

function App() {
  const today = new Date();
  const dayname = ['일','월','화','수','목','금','토','일'];       
  const todayDateHTML = <p>오늘은 <br></br> {today.getMonth()+1}월 {today.getDate()}일 {dayname[today.getDay()]}요일</p>;

  const [ darkTheme, setDarkTheme ] = useState(false); // 다크 모드

  const todayHTML =         
  <div className="component">
    <h1>오늘 할 일은?</h1>
    {todayDateHTML}
    <TodoListTemplate darkTheme={darkTheme}></TodoListTemplate>
    <p style={{ color : "gray" }}>요일별로 할 일이 자동으로 갱신됩니다!</p>
  </div>

  const preferenceHTML =
  <div>
    <div className="component">
        <h1>설정</h1>
        <p>요일별 할 일 설정</p>
        <DayDoListTemplate darkTheme={darkTheme}></DayDoListTemplate>
        <p style={{ color : "gray" }}>요일별로 할 일을 설정하면<br></br> 해당 요일에 자동으로 할 일이 갱신됩니다!</p>
    </div>
    <div className="component"> 

      <p style={{ color : "gray" }}>설정 추가중...</p>
    </div>
  </div>

  const [ selectedPageHTML, setSelectedPageHTML ] = useState(todayHTML); // 기본값: 오늘 할 일

  useEffect(() => { // 랜더링 이후
    const body = document.querySelector('body');
    const navigation = document.querySelector('.navigation');
    const footer = document.querySelector('.footer');

    if(darkTheme) {  
      body.classList.add('dark');
      navigation.classList.add('dark');
      footer.classList.add('dark');
    }
    else {
      body.classList.remove('dark');
      navigation.classList.remove('dark');
      footer.classList.remove('dark');
    }
  }, [ darkTheme ]);

  function handleNavigation(e) {
      const selectedPageName = e.target.className;

      if(selectedPageName === "today") {
        setSelectedPageHTML(todayHTML);
      }
      else if(selectedPageName === "preference") {
        setSelectedPageHTML(preferenceHTML);
      }
  }

  function handleThemeToggle() {
    setDarkTheme(!darkTheme);
  }

  // 메인
  return (
    <div className="main">
      <Navigation onNavigation={handleNavigation}></Navigation>
      <Preference darkTheme={darkTheme} onToggle={handleThemeToggle}></Preference>
      {selectedPageHTML}
      <Footer></Footer>
    </div>
  );
}

export default App;
