import { useEffect, useState } from 'react';
import TodoItem from './TodoItem';
import './TodoItemList.css';

const TodoItemList = ({ todoItemList, onToggle, onRemove }) => {
    const todoListHTML = todoItemList.map(
        ({id, content, isDone}) => {
            <TodoItem
                key={id}
                id={id}
                content={content}
                isDone={isDone}
                onToggle={onToggle}
                onRemove={onRemove}
            />
        }
    );
 
    return (
        <div className="todo-list-wrapper">
            {todoListHTML}
        </div>
    );
}

export default TodoItemList;
