import './DaydoSlider.css'

const DaydoSlider = ({ day, onSlide}) => {
    function handleDay() {
        const dayname = ['일','월','화','수','목','금','토'];
        
        return dayname[day] + '요일';
    }
    
    return (
        <div className="daydo-title">
            <h2><a className="left" onClick={onSlide}>&#60;</a></h2>
            <h2>{handleDay()}</h2>
            <h2><a className="left" onClick={onSlide}>&#62;</a></h2>
    </div>
    );
}

export default DaydoSlider;
