import DaydoItem from './DaydoItem';
import './DaydoItemList.css';

const DaydoItemList = ({ todoList, onRemove, onChange }) => {
    let todoListHTML;

    if(!todoList) {
        todoListHTML = <p>로딩중...</p>;
    }
    else if(todoList.length === 0) {
        todoListHTML = <p>**일에 할 일을 적어주세요!</p>
    }
    else {
        todoListHTML = todoList.map(
            ({id, content, isDone}) => {
                return <DaydoItem
                    key={id}
                    id={id}
                    content={content}
                    onRemove={onRemove}
                    onChange={onChange}
                />
            }
        );
    }

    return (
        <div className="todo-list-wrapper">
            {todoListHTML}
        </div>
    );
}

export default DaydoItemList;
