import './Preference.css';

const Preference = () => {
    let dark_theme = false;

    function onToggle() {
        // CSS에서 바뀜
        const navigation = document.querySelector('.navigation');
        const body = document.querySelector('body');
        const footer = document.querySelector('.footer');
        const template_wrappers = document.querySelectorAll('.template-wrapper');

        navigation.classList.toggle("dark");
        body.classList.toggle("dark");
        footer.classList.toggle("dark");

        if(!dark_theme) {
            template_wrappers.forEach(template_wrapper => {
                template_wrapper.classList.remove("template-wrapper");
                template_wrapper.classList.add("template-wrapper-dark");
            });
        }
        else {
            template_wrappers.forEach(template_wrapper => {
                template_wrapper.classList.remove("template-wrapper-dark");
                template_wrapper.classList.add("template-wrapper");
            });
        }

        dark_theme = !dark_theme;
    }

    return (
        <div className="template-wrapper preference-template-wrapper">
            <div className="title">
                <h2>기본 설정</h2>
                <p></p>
            </div>
            <div className="content">
                <ul className="general">
                    <li className="theme">
                        <span className="text">다크 테마</span>
                        <label className="switch">
                            <input type="checkbox" name="theme" onChange={onToggle}></input>
                            <span className="slider round"></span>
                        </label>
                    </li>
                </ul>
            </div>
        </div>
    );
}

export default Preference;
